import React from 'react';
import './index.css';
import background from './background.jpeg';
import { Spring } from 'react-spring';


const styles = {
    backgroundImage: `url(${background})`,
    height: '88vh',
    backgroundSize: 'cover',
    weight: '1000px !important'
}

class Header extends React.Component {
    render(){
        return (
            <Spring
             from={{ opacity: 0 }}
             to = {{ opacity: 1 }}
            >
                { props => (
                    <div style={props}>
                        <header style={styles} className="header">
                        <div className="title">
                        <h1 className="name">Raz Levy</h1>
                        <hr size="2" className="header-hr"/>
                        <span className="subtitle">Full Stack Developer</span>
                        <br />
                        <div className="frameworks-header">ReactJS, NodeJS, HTML, CSS, C, C++, SQL</div>
                        </div>
                        </header>
                    </div>
                )}
            </Spring>
        );
    }
}

export default Header;
